function assertHelpers() {
	let helpers = {};

    // Returns the specified response header
	helpers.getHeader = function getHeader(response, name) {
        name = name.toLowerCase();
        let header = response.headers.find(({ key }) => key.toLowerCase() === name);
        if (header) {
            return header.value;
        }
    },

    // Asserts that the response was successful and has the specified status code
	helpers.assertSuccessfulResponse = function assertSuccessfulResponse(response, status) {
		pm.expect(response).to.be.an('object');
        pm.expect(response.code).to.be.a('number').at.least(200).and.below(400);
        pm.expect(response.code).to.equal(status);

        let contentType = helpers.getHeader(response, 'content-type');
        pm.expect(contentType).to.be.a('string').and.include('application/json');
	},

    // Asserts that the response was an error and has the specified status code
	helpers.assertErrorResponse = function assertErrorResponse(response, status) {
		pm.expect(response).to.be.an('object');
        pm.expect(response.code).to.be.a('number').at.least(400).and.below(600);
        pm.expect(response.code).to.equal(status);

        let contentType = getHeader(response, 'content-type');
        pm.expect(contentType).to.be.a('string').and.include('application/json');
	}, 

    // Asserts Validates the given object against the JSON Schema
    helpers.assertJSONSchema = function assertJSONSchema(data, schema) {
        let isValid = tv4.validate(data, schema);
        if (!isValid) {
            throw tv4.error;
        }
    }

	return helpers;
}; assertHelpers();

function loadHelpers() {
	let helpers = {};

	helpers.generateString = function generateString(length) {
		var result           = '';
        var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        var charactersLength = characters.length;

        for ( var i = 0; i < length; i++ ) {
            result += characters.charAt(Math.floor(Math.random() * charactersLength));
        }

        return result;
	},

	helpers.getFirstName = function getFirstName() {
		var list = [ "Abraham", "Andrew", "Benjamin", "Bill", "Calvin", "Chester", "Dwight", "Franklin", "George", "Gerald" ];
        var random = 0 + (Math.round(Math.random() * (list.length - 1 - 0)));
        return list[random];
	}

	return helpers;
}; loadHelpers();

